	 ('0b8aba8c0d1c322588c7786f420d0717',2,4,'哈ッ！ あっ…発声練習を…',NULL,'あいさつ','マイスタジオ'),
	 ('0b8aba8c0d1c322588c7786f420d0717',2,5,'あたしの曲、○○さんも覚えてくれましたか♪',NULL,'あいさつ','マイスタジオ'),
	 ('0b8aba8c0d1c322588c7786f420d0717',2,7,'仕事もプライベートも一直線！',NULL,'お仕事','お仕事'),
	 ('0b8aba8c0d1c322588c7786f420d0717',2,8,'ひらひら～♪どうですか？',NULL,'お仕事','お仕事'),
	 ('0b8aba8c0d1c322588c7786f420d0717',2,9,'あたしのエナジー、全力ですっ！',NULL,'お仕事','お仕事'),
	 ('0b8aba8c0d1c322588c7786f420d0717',2,10,'○○さんと、もっと頑張ります！ お仕事も、こ、恋も…？',NULL,'お仕事','お仕事'),
	 ('0b8aba8c0d1c322588c7786f420d0717',2,11,'○○さんとならもっと強くなれます！ アイドルも空手も！',NULL,'お仕事','お仕事（親愛度UP）'),
	 ('0b8aba8c0d1c322588c7786f420d0717',2,12,'この歌で真っ向勝負です！',NULL,'お仕事','LIVEバトル'),
	 ('0b8aba8c0d1c322588c7786f420d0717',2,14,'空手とアイドルの両立で悩んだ事もあったけど…さんの支えがあったから、今このステージにいられるんだと思います！',NULL,'親愛度MAX','親愛度MAX演出');
INSERT INTO public."TB_IDOLS_SCRIPTS" (card_hash,idol_id,script_id,card_script,trans_script,script_category,script_group) VALUES
	 ('0b8aba8c0d1c322588c7786f420d0717',2,15,'ステージでも、空手と、○○さんからの教えは忘れません！',NULL,'親愛度MAX','マイスタジオ（親愛度MAX）'),
	 ('0b8aba8c0d1c322588c7786f420d0717',2,16,'今日も○○さんとともに、精進します！ 行きますよ！',NULL,'親愛度MAX','お仕事（親愛度MAX）'),
	 ('fd6229dc5e213996f19792128e1de279',2,5,'ふふっ…決まってますよね',NULL,'あいさつ','マイスタジオ'),
	 ('1e636fde2ac167157ac7a3110085f226',2,2,'あたしのパフォーマンスも、誰かを支えるものになったら嬉しいですっ！みんなー！がんばってーっ！フレー、フレー！',NULL,'加入',''),
	 ('1e636fde2ac167157ac7a3110085f226',2,4,'振り付けもコールも、今日に備えて特訓してきました！ その特訓で学んだことを、この本番の舞台で、思い切りぶつけてきます！ ○○さんも、あたしのパフォーマンスを楽しんでくださいねっ！',NULL,'あいさつ','アイドルコメント'),
	 ('1e636fde2ac167157ac7a3110085f226',2,5,'運動会も、真っ向勝負ですっ！',NULL,'あいさつ','マイスタジオ'),
	 ('1e636fde2ac167157ac7a3110085f226',2,6,'気合いを入れて、可愛らしくっ！',NULL,'あいさつ','マイスタジオ'),
	 ('1e636fde2ac167157ac7a3110085f226',2,7,'応援は、確かな力になりますよ！',NULL,'あいさつ','マイスタジオ'),
	 ('1e636fde2ac167157ac7a3110085f226',2,8,'えへっ、今回の振り付けは、あたしもアイディアを出したんですよ！',NULL,'あいさつ','マイスタジオ'),
	 ('1e636fde2ac167157ac7a3110085f226',2,10,'鍛えた心と体を活かしますっ！',NULL,'お仕事','お仕事');
INSERT INTO public."TB_IDOLS_SCRIPTS" (card_hash,idol_id,script_id,card_script,trans_script,script_category,script_group) VALUES
	 ('1e636fde2ac167157ac7a3110085f226',2,11,'正拳上段突きで応援しますっ！',NULL,'お仕事','お仕事'),
	 ('1e636fde2ac167157ac7a3110085f226',2,12,'どんなときも可愛く…ですっ♪',NULL,'お仕事','お仕事'),
	 ('1e636fde2ac167157ac7a3110085f226',2,13,'照れずに、堂々と…道着を着ているつもりで大胆に踊ります！',NULL,'お仕事','お仕事'),
	 ('1e636fde2ac167157ac7a3110085f226',2,14,'○○さんも声を出して！ 一緒に応援しましょうっ！',NULL,'お仕事','お仕事（親愛度UP）'),
	 ('1e636fde2ac167157ac7a3110085f226',2,15,'気持ちは、通じるはず…！',NULL,'お仕事','LIVEバトル'),
	 ('1e636fde2ac167157ac7a3110085f226',2,17,'声援を飛ばす○○さんは、素敵な笑顔でした｡そのとき思ったんです。あたし、いい人に巡り合えたってっ！ ふふっ♪',NULL,'親愛度MAX','親愛度MAX演出'),
	 ('1e636fde2ac167157ac7a3110085f226',2,18,'あたしの応援が、○○さんにも響いたら…大成功です！',NULL,'親愛度MAX','マイスタジオ（親愛度MAX）'),
	 ('1e636fde2ac167157ac7a3110085f226',2,19,'ファンのみんなに、力をもらいます。えぇ、○○さんからも',NULL,'親愛度MAX','お仕事（親愛度MAX）'),
	 ('7fe5882d01aa44bbd57c2a3e4bf8307d',2,1,'こんなところで立ち止まっている場合じゃないのに！綱が絡まって、思うように進めません!…くっ、こういうときこそ焦りは禁物！○○さんと学んだことを思い出せば、きっと…！',NULL,'あいさつ','アイドルコメント'),
