INSERT INTO public."TB_IDOLS_SCRIPTS" (card_hash,idol_id,script_id,card_script,trans_script,script_category,script_group) VALUES
	 ('7fe5882d01aa44bbd57c2a3e4bf8307d',2,15,'ここから逆転できたら、○○さんも驚くかも…よ～し！',NULL,'親愛度MAX','マイスタジオ（親愛度MAX）'),
	 ('7fe5882d01aa44bbd57c2a3e4bf8307d',2,16,'○○さんが応援してくれたら、優勝間違いなしです！',NULL,'親愛度MAX','お仕事（親愛度MAX）'),
	 ('3822324e5238a535b49fd5be10e0f985',2,1,'ふぅ～…いい湯だったぁ…。身も心も綺麗に清めて、リラックス完了…。けど…あの、○○さん、やっぱりあたし、変ですか？そんなに見られると…。え、えっと…もう一回入り直そうかな…',NULL,'あいさつ','アイドルコメント'),
	 ('3822324e5238a535b49fd5be10e0f985',2,4,'色浴衣…選んでみたんです',NULL,'あいさつ','マイスタジオ'),
	 ('3822324e5238a535b49fd5be10e0f985',2,5,'あ、まだ濡れてて…。風邪引かないように、あとで乾かそうかと',NULL,'あいさつ','マイスタジオ'),
	 ('3822324e5238a535b49fd5be10e0f985',2,9,'お湯も滴る女の子らしさ、とか',NULL,'お仕事','お仕事'),
	 ('3822324e5238a535b49fd5be10e0f985',2,10,'瞑想しながら湯船に浸かっていたら、のぼせかけました…',NULL,'お仕事','お仕事'),
	 ('3822324e5238a535b49fd5be10e0f985',2,11,'この髪型、○○さんに見せるのは初めて…ですねっ',NULL,'お仕事','お仕事（親愛度UP）'),
	 ('3822324e5238a535b49fd5be10e0f985',2,12,'湯に映った月、打ちます！',NULL,'お仕事','LIVEバトル'),
	 ('3822324e5238a535b49fd5be10e0f985',2,14,'湯上り姿を見つめられたら、照れます…。プロデューサーさんも湯上りだから、ほっぺが赤いです。二人でぽかぽか、ですね',NULL,'親愛度MAX','親愛度MAX演出');
INSERT INTO public."TB_IDOLS_SCRIPTS" (card_hash,idol_id,script_id,card_script,trans_script,script_category,script_group) VALUES
	 ('3822324e5238a535b49fd5be10e0f985',2,15,'フルーツ牛乳、コーヒー牛乳。どっちも飲みたいですよね。半分こします？',NULL,'親愛度MAX','マイスタジオ（親愛度MAX）'),
	 ('3822324e5238a535b49fd5be10e0f985',2,16,'プロデューサーさんも、湯上りツルツルたまご肌。お揃いですねっ',NULL,'親愛度MAX','お仕事（親愛度MAX）'),
	 ('b1976493a44373a42a7087ea012da84a',2,3,'いつもと違うあたしで…！',NULL,'あいさつ','マイスタジオ'),
	 ('b1976493a44373a42a7087ea012da84a',2,7,'もっと楽しみ尽くしましょう！',NULL,'お仕事','お仕事'),
	 ('b1976493a44373a42a7087ea012da84a',2,8,'体の芯から熱くなってますっ！',NULL,'お仕事','お仕事'),
	 ('b1976493a44373a42a7087ea012da84a',2,1,'ここまでノンストップだったけど、会場のみんなはまだ元気いっぱいですよね？あたしも、まだまだ歌い足りません！このみなぎる気力は、温泉パワーのおかげ。さぁ、LIVE本番はここからですっ！',NULL,'あいさつ','アイドルコメント'),
	 ('b1976493a44373a42a7087ea012da84a',2,2,'帯があると落ち着きます！',NULL,'あいさつ','マイスタジオ'),
	 ('b1976493a44373a42a7087ea012da84a',2,4,'和の心に包まれて…嬉しいっ！',NULL,'あいさつ','マイスタジオ'),
	 ('b1976493a44373a42a7087ea012da84a',2,5,'ステージで揺らいだりしないように、臍下丹田を意識して…立つ！',NULL,'あいさつ','マイスタジオ'),
	 ('b1976493a44373a42a7087ea012da84a',2,9,'演武のように綺麗な動きでっ！',NULL,'お仕事','お仕事');
INSERT INTO public."TB_IDOLS_SCRIPTS" (card_hash,idol_id,script_id,card_script,trans_script,script_category,script_group) VALUES
	 ('b1976493a44373a42a7087ea012da84a',2,10,'温泉と同じくらい熱いLIVE！歌もお風呂も楽しんでください！',NULL,'お仕事','お仕事'),
	 ('b1976493a44373a42a7087ea012da84a',2,11,'この歌は正拳突き！受け取ってください、プロデューサーさん！',NULL,'お仕事','お仕事（親愛度UP）'),
	 ('b1976493a44373a42a7087ea012da84a',2,12,'ステージで使える技も…ある！',NULL,'お仕事','LIVEバトル'),
	 ('b1976493a44373a42a7087ea012da84a',2,14,'プロデューサーさんの湯は、魔法の湯。その熱でみるみる可愛くなって、ステージで戦う勇気ももらえます。あたしが、その証です！',NULL,'親愛度MAX','親愛度MAX演出'),
	 ('b1976493a44373a42a7087ea012da84a',2,15,'プロデューサーさんの指導を胸に、熱く、可愛くキメてみせます！',NULL,'親愛度MAX','マイスタジオ（親愛度MAX）'),
	 ('b1976493a44373a42a7087ea012da84a',2,16,'温泉より熱い心…あたしも、そしてプロデューサーさんも！',NULL,'親愛度MAX','お仕事（親愛度MAX）'),
	 ('7bd7c11789dc75e0d11d3eddab7ad46b',2,1,'LIVEに向け、日々精進あるのみですね。可愛く！元気な！ステージに、どうかご期待ください！○○さんと交わした、最強のアイドルになるという誓い…今もこの胸に燃えています！押忍！！',NULL,'あいさつ','アイドルコメント'),
	 ('7bd7c11789dc75e0d11d3eddab7ad46b',2,4,'最強。答えはまだ、わかりません',NULL,'あいさつ','マイスタジオ'),
	 ('7bd7c11789dc75e0d11d3eddab7ad46b',2,5,'今日のあたしは、昨日より可愛く。一歩一歩、進んでいきます！',NULL,'あいさつ','マイスタジオ'),
	 ('7bd7c11789dc75e0d11d3eddab7ad46b',2,9,'呼吸を整え、力を込めて…良し！',NULL,'お仕事','お仕事');
INSERT INTO public."TB_IDOLS_SCRIPTS" (card_hash,idol_id,script_id,card_script,trans_script,script_category,script_group) VALUES
	 ('7bd7c11789dc75e0d11d3eddab7ad46b',2,10,'これは…武者震い！本番、いつでも大丈夫！望むところです！',NULL,'お仕事','お仕事'),
	 ('7bd7c11789dc75e0d11d3eddab7ad46b',2,11,'○○さんい恥じないLIVEを、必ずお見せします！',NULL,'お仕事','お仕事（親愛度UP）'),
	 ('7bd7c11789dc75e0d11d3eddab7ad46b',2,12,'この拳は…夢を叶える拳！',NULL,'お仕事','LIVEバトル'),
	 ('7bd7c11789dc75e0d11d3eddab7ad46b',2,14,'もし、誓いを果たせたら…その時は、新しい夢を誓わせてください。そしてまた、○○さんとあたしで目指しましょう！',NULL,'親愛度MAX','親愛度MAX演出'),
	 ('7bd7c11789dc75e0d11d3eddab7ad46b',2,15,'あたしの誓いは、○○さんと一緒に果たしてみせます！',NULL,'親愛度MAX','マイスタジオ（親愛度MAX）'),
	 ('7bd7c11789dc75e0d11d3eddab7ad46b',2,16,'拳は、色々なものをつかめますね。○○さんのことも…',NULL,'親愛度MAX','お仕事（親愛度MAX）'),
	 ('2b1e910d31405f062817d9e0e3e285fe',2,1,'握り拳に生じた、マイク一本分の余裕。それが、あたしを成長させてくれました。強く、可愛く、磨きをかけたアイドル中野有香を、どうかご覧ください！では、今日は握り拳ではなく…ピースサインで！',NULL,'あいさつ','アイドルコメント'),
	 ('2b1e910d31405f062817d9e0e3e285fe',2,2,'目指す歌は、もちろん一撃必殺！',NULL,'あいさつ','マイスタジオ'),
	 ('2b1e910d31405f062817d9e0e3e285fe',2,3,'初心を忘れず、さらに先へ！',NULL,'あいさつ','マイスタジオ'),
	 ('2b1e910d31405f062817d9e0e3e285fe',2,4,'フリル、すっごく可愛い…幸せ…！',NULL,'あいさつ','マイスタジオ');
INSERT INTO public."TB_IDOLS_SCRIPTS" (card_hash,idol_id,script_id,card_script,trans_script,script_category,script_group) VALUES
	 ('2b1e910d31405f062817d9e0e3e285fe',2,5,'拳歌一如。歌は突き出す拳のようにまっすぐ。でも、笑顔でピース！',NULL,'あいさつ','マイスタジオ'),
	 ('2b1e910d31405f062817d9e0e3e285fe',2,7,'鍛錬は、裏切らない！はずです！',NULL,'お仕事','お仕事'),
	 ('2b1e910d31405f062817d9e0e3e285fe',2,8,'夢のステージ…でも、夢じゃない！',NULL,'お仕事','お仕事'),
	 ('2b1e910d31405f062817d9e0e3e285fe',2,9,'コールに感謝！気合入りました！',NULL,'お仕事','お仕事'),
	 ('2b1e910d31405f062817d9e0e3e285fe',2,10,'あたしの手は、マイクだけではなく、みなさんの心だって握れます！',NULL,'お仕事','お仕事'),
	 ('2b1e910d31405f062817d9e0e3e285fe',2,11,'○○さん！今日のあたし、歌うほど力が湧いてきます！',NULL,'お仕事','お仕事（親愛度UP）'),
