INSERT INTO public."TB_IDOLS_SCRIPTS" (card_hash,idol_id,script_id,card_script,trans_script,script_category,script_group) VALUES
	 ('fd6229dc5e213996f19792128e1de279',2,3,'ハートのワンポイントで、可愛く…えへへ。プライベートではこういうの、あまりつけないから…なんだか緊張しますね。でも○○さんがほめてくれると、可愛いあたしもいいかなって思えます！',NULL,'あいさつ','アイドルコメント'),
